import React, { Component, useState } from 'react';
import background from "./sudoku.jpg"
import particlesConfig from "../config/particles-config"
import ParticlesBackground from "./ParticlesBackground"
import { useCallback } from "react";
import Particles from "react-particles";
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import axios from 'axios'


class Upload extends Component {

    // const [userInfo, setuserInfo] = useState({
    //     file: [],
    // });

    state = {
        selectedFile: null,
        textOne: "Select Image",
        textTwo: "click here to upload",
        file: []
    }

    fileSelectHandler = (event) => {
        this.setState({
            selectedFile: event.target.files[0],
            textOne: "File Selected:",
            textTwo: event.target.files[0].name,
            file: event.target.files[0]
        });
        console.log(event.target.files[0]);
        // setuserInfo({
        //     ...userInfo,
        //     file: event.target.files[0],
        // });
    }

    fileUploadHandler = () => {
        this.setState({
            textOne: "Select Image",
            textTwo: "click here to upload"
        })
        localStorage.setItem('image', this.state.selectedFile);
        console.log(localStorage.get('image'));

        const formdata = new FormData();
        formdata.append('file', this.state.file);

        axios.post("http://localhost:8080/imageupload", formdata, {
            headers: {"Content-Type": "multipart/form-data"}
        })
        .then(res => {
            console.warn(res);
        })
    }

    render() {
        return (
                <div className = "upload">
                <ParticlesBackground />
                <Container component="main" maxWidth="xs">
                <Box>
                    <section>
                    <label style={{color: "#FFFFFF"}}>
                        {this.state.textOne}
                        <br />
                        <span>{this.state.textTwo}</span>
                        <input
                            type = "file"
                            name = "images"
                            onChange = {this.fileSelectHandler}
                            multiple accept = "image/png, image/jpeg"
                        />
                    </label>
                    </section>
                </Box>
                <Box>
                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        href="/Upload"
                        onClick={this.fileUploadHandler}
                        sx={{ mt: 20, mb: 2 }}
                    >
                    Upload
                    </Button>
                </Box>
            </Container>
        </div>
        );
    }
}

export default Upload

